import React, { useContext } from 'react';
import { NavLink } from 'react-router-dom';

import { CurrentUserContext } from 'contexts/current-user';

const FeedToggler = ({ tag }) => {
  const [{ isLogged }] = useContext(CurrentUserContext);

  return(
    <div className="feed-toggle">
      <ul className="nav nav-pills outline-active">
        {
          isLogged && (
            <li className="nav-item">
              <NavLink to="/feed" className="nav-link">Your feed</NavLink>
            </li>
          )
        }
        <li className="nav-item">
          <NavLink to="/" className="nav-link" exact>Global feed</NavLink>
        </li>
        {
          tag && (
            <li className="nav-item">
              <NavLink to={`/tags/${tag}`} className="nav-link">
                <i className="ion-pound"></i>
                {tag}
              </NavLink>
            </li>
          )
        }
      </ul>
    </div>
  );
}

export default FeedToggler;