import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import useFetch from 'hooks/use-fetch';

const Auth = ({ match }) => {
  const isLoginPage = match.path === '/login';
  const pageTitle = isLoginPage ? 'Sign In' : 'Sign Up';
  const descriptionText = isLoginPage ? 'Need an account?' : 'Have an account?';
  const descriptionLink = isLoginPage ? '/register' : '/login';
  const apiUrl = isLoginPage ? '/users/login' : '/users';

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [username, setUsername] = useState('');
  const [{isLoading, response, error}, doFetch] = useFetch(apiUrl);

  const handleSubmit = (e) => {
    e.preventDefault();

    const user = isLoginPage ? {email, password} : {username, email, password}

    doFetch({
      method: 'POST',
      data: {
        user
      }
    });
  }

  return(
    <div className="auth-page">
      <div className="container page">
        <div className="row">
          <div className="col-md-6 offset-md-3 col-xs-12">
            <h1 className="text-xs-center">{pageTitle}</h1>
            <p className="text-xs-center">
              <Link to={descriptionLink}>{descriptionText}</Link>
            </p>
            <form onSubmit={handleSubmit}>
              <fieldset>
                {
                  !isLoginPage && (
                    <fieldset className="form-group">
                      <input
                        name="username"
                        type="text" 
                        className="form-control form-control-lg" 
                        placeholder="Username"
                        onChange={(e) => setUsername(e.target.value)}
                      />
                    </fieldset>
                  )
                }
                <fieldset className="form-group">
                  <input
                    name="email"
                    type="email" 
                    className="form-control form-control-lg" 
                    placeholder="Email"
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </fieldset>
                <fieldset className="form-group">
                  <input
                    name="password"
                    type="password" 
                    className="form-control form-control-lg" 
                    placeholder="Password" 
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </fieldset>
                <button 
                  className="btn btn-lg btn-primary pull-xs-right" 
                  disabled={isLoading}
                >
                  {pageTitle}
                </button>  
              </fieldset>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Auth;