import React, { useEffect, Fragment } from 'react';
import { stringify } from 'query-string';

import useFetch from 'hooks/use-fetch';
import ArticleRender from 'components/article-render';
import Pagination from 'components/pagination';
import Loading from 'components/loading';
import PopularTags from 'components/popular-tags';
import FeedToggler from 'components/feed-toggler';
import { getPaginator, limit } from 'utils';

const TagFeed = ({ location, match }) => {
  const tagName = match.params.slug;
  const { currentPage, offset } = getPaginator(location.search);
  const stringifedParams = stringify({ limit, offset, tag: tagName });
  const apiUrl = `/articles?${stringifedParams}`;
  const [{ isLoading, response, error}, doFetch] = useFetch(apiUrl);

  useEffect(() => {
    doFetch();
  }, [doFetch, currentPage, tagName]);

  return(
    <div className="home-page">
      <div className="banner">
        <div className="container">
          <h1>Medium clone</h1>
          <p>A place to share knowledge</p>
        </div>
      </div>
      <div className="container page">
        <div className="row">
          <div className="col-md-9">
            <FeedToggler tag={tagName} />
            {isLoading && <Loading />}
            {error && <div>We have error here</div>}
            {!isLoading && response && (
              <Fragment>
                <ArticleRender articles={response.articles} />
                <Pagination 
                  total={response.articlesCount} 
                  limit={limit} 
                  url={match.url} 
                  currentPage={currentPage} 
                />
              </Fragment>
            )}
          </div>
          <article className="col-md-3">
            <PopularTags />
          </article>
        </div>
      </div>
    </div>
  );
}

export default TagFeed;